import React, {useEffect, useState} from 'react';
import './App.css';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from '@chatscope/chat-ui-kit-react';

function App() {
const [messages, setMessages]=useState([
  {
    message: "Hello I am health bot",
    sender:"Health Bot"
  }
]);

const [typing, setTyping]= useState(false)

//message below is event object
const handleSend=async(message)=>{
  const newMessage={
    message: message,
    sender: "user",
    direction: 'outgoing'
  }

//update the messages array to display latest message
//using below method because we cant update the state messages array using setMessages directly on message object
const newMessages= [...messages, newMessage]
setMessages(newMessages)
setTyping(true)


//set a typing indicator when bot types
//send the message object to backend so that bot can respond

}

//Initiallly the messages array will have this initial message 
//Each message is an object having two elements
//then we render this array in MessagesList ui component to show the messages we have in the array
  return (
    <div className="App">
     <div style={{position:"relative",}}>
      <MainContainer>
        <ChatContainer>
          <MessageList typingIndicator={typing? <TypingIndicator content="bot is typing"/>: null}>
            {messages.map((message, i)=>{
              return <Message key={i} model={message}/>
            })}
          </MessageList>
          <MessageInput  placeholder='Ask me something' onSend={handleSend}/>
        </ChatContainer>
      </MainContainer>
     </div>
    </div>
  );
}

export default App;
